	$.fn.defaultText = function(value){
		
		var element = this.eq(0);
		
		element.data('defaultText',value);
		
		element.focus(function(){
			if(element.val() == value){
				element.val('').removeClass('defaultText');
			}
		}).blur(function(){
			if(element.val() == '' || element.val() == value){
				element.addClass('defaultText').val(value);
			}
		});
		
		return element.blur();
	}
	
	
	$.tzineGET = function(subact,data,func){
		
		if($.isFunction(data)) {
			func = data;
			data = {};
		}
		
		$.get(ajxURL,{action:'tzine_ajax',subaction:subact,data:data},func,'json');	
	}
	
	$.tzinePOST	= function(subact,data,func){
		
		if($.isFunction(data)) {
			func = data;
			data = {};
		}
		
		$.post(ajxURL+'?action=tzine_ajax&subaction='+subact,{data:data},func,'json');
	}
	

	if($('#slider').length)
	{
		$('#slider').nivoSlider({
			effect:'fold,fade',
			directionNav:false,
			pauseTime:10000,
			controlNavThumbs:false,
			afterChange: function(){
				var index = nivoControls.filter('.active').index();
				$('#sliderThumbnails a').removeClass('active').eq(index).addClass('active');
			}
		});
		
		var nivoControls = $('#slider a.nivo-control');	
		
		$('#sliderThumbnails a').each(function(i){
			var elem = $(this);
	
			elem.click(function(){
				nivoControls.eq(i).click();
				return false;
			});
			
			var span = $('<span class="pointy"><span class="pointyShadow"></span><span class="pointyFrontTip"></span></span>');
			elem.prepend(span);
			
			if(i==0) elem.addClass('active');
			
		});
	}

